palavras = str(input()).split()

total = []

def all_vogais(word):
    soma = 0
    for e in word:
        if e == 'a':
            soma += 1
        elif e == 'e':
            soma += 1
        elif e == 'i':
            soma += 1
        elif e == 'o':
            soma += 1
        elif e == 'u':
            soma += 1

    if soma >= 5:
        return word

for palavra in palavras:
    total += [all_vogais(palavra)]


s = 0
for x in total:
    if x != None:
        s += 1

print(f'Quantidade de pentavogalicas: {s}')
